import React, { useEffect, useState } from 'react';
import axios from '../services/api';
import { useNavigate } from 'react-router-dom';

function BookList() {
  const [books, setBooks] = useState([]);
  const navigate = useNavigate();

  const loadBooks = async () => {
    const res = await axios.get('/api/books');
    setBooks(res.data);
  };

  const deleteBook = async (isbn) => {
    await axios.delete(`/api/books/${isbn}`);
    loadBooks();
  };

  useEffect(() => {
    loadBooks();
  }, []);

  return (
    <div style={{ textAlign: 'center' }}>
      <h2>Books</h2>
      <button onClick={() => navigate('/add')}>Add Book</button>
      <ul style={{ listStyle: 'none', padding: 0 }}>
        {books.map(book => (
          <li key={book.isbn} style={{ margin: '10px 0' }}>
            <b>{book.title}</b> by {book.author} ({book.publicationYear})
            <button onClick={() => navigate(`/edit/${book.isbn}`)} style={{ marginLeft: '10px' }}>Edit</button>
            <button onClick={() => deleteBook(book.isbn)} style={{ marginLeft: '5px' }}>Delete</button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default BookList;
