import React, { useEffect, useState } from 'react';
import axios from '../services/api';
import { useNavigate } from 'react-router-dom';

function BookList() {
  const [books, setBooks] = useState([]);
  const navigate = useNavigate();

  // ✅ Load all books on component mount
  const loadBooks = async () => {
    try {
      const response = await axios.get('/api/books');
      setBooks(response.data);
      console.log("Books loaded:", response.data);
    } catch (error) {
      console.error("Failed to load books:", error);
      alert("Failed to load books.");
    }
  };

  // ✅ Delete a book by ISBN
  const deleteBook = async (isbn) => {
    if (!window.confirm(`Are you sure you want to delete book with ISBN ${isbn}?`)) return;

    try {
      await axios.delete(`/api/books/${isbn}`);
      alert("Book deleted successfully!");
      loadBooks(); // reload after delete
    } catch (error) {
      console.error("Error deleting book:", error);
      alert("Failed to delete book.");
    }
  };

  // ✅ useEffect to load books when component is mounted
  useEffect(() => {
    loadBooks();
  }, []);

  return (
    <div style={{ textAlign: 'center', padding: '20px' }}>
      <h2>Book List</h2>
      <button onClick={() => navigate('/add')}>Add Book</button>
      <br /><br />

      {books.length === 0 ? (
        <p>No books found.</p>
      ) : (
        <ul style={{ listStyle: 'none', padding: 0 }}>
          {books.map((book) => (
            <li
              key={book.isbn}
              style={{
                marginBottom: '20px',
                padding: '10px',
                border: '1px solid #ddd',
                borderRadius: '5px',
                maxWidth: '500px',
                margin: '0 auto 15px auto'
              }}
            >
              <h3>{book.title}</h3>
              <p>
                <strong>Author:</strong> {book.author}<br />
                <strong>Year:</strong> {book.publicationYear}<br />
                <strong>ISBN:</strong> {book.isbn}
              </p>
              <button onClick={() => navigate(`/edit/${book.isbn}`)}>Edit</button>
              <button onClick={() => deleteBook(book.isbn)} style={{ marginLeft: '10px' }}>Delete</button>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}

export default BookList;
