import React, { useEffect, useState } from 'react';
import axios from '../services/api';
import { useNavigate, useParams } from 'react-router-dom';

function EditBook() {
  const [book, setBook] = useState({
    isbn: '',
    title: '',
    author: '',
    publicationYear: ''
  });

  const { isbn } = useParams(); // get :isbn from URL
  const navigate = useNavigate();

  // Load the book details when component mounts
  useEffect(() => {
    console.log("Fetching book with ISBN:", isbn);
    axios.get(`/api/books/${isbn}`)
      .then(res => {
        setBook(res.data);
      })
      .catch(err => {
        console.error("Error loading book:", err);
        alert("Failed to load book. Please check if the ISBN exists.");
      });
  }, [isbn]);

  // Handle update
  const handleUpdate = async () => {
    try {
      await axios.put(`/api/books/${isbn}`, book);
      alert("Book updated successfully!");
      navigate('/books');
    } catch (err) {
      console.error("Update failed:", err);
      alert("Failed to update book.");
    }
  };

  return (
    <div style={{ textAlign: 'center' }}>
      <h2>Edit Book</h2>
      <input value={book.isbn} disabled />
      <br /><br />
      <input
        type="text"
        value={book.title}
        onChange={(e) => setBook({ ...book, title: e.target.value })}
        placeholder="Title"
      />
      <br /><br />
      <input
        type="text"
        value={book.author}
        onChange={(e) => setBook({ ...book, author: e.target.value })}
        placeholder="Author"
      />
      <br /><br />
      <input
        type="number"
        value={book.publicationYear}
        onChange={(e) => setBook({ ...book, publicationYear: e.target.value })}
        placeholder="Publication Year"
      />
      <br /><br />
      <button onClick={handleUpdate}>Update</button>
    </div>
  );
}

export default EditBook;
