import React, { useEffect, useState } from 'react';
import axios from '../services/api';
import { useNavigate } from 'react-router-dom';

function BookList() {
  const [books, setBooks] = useState([]);
  const navigate = useNavigate();

  // ✅ Fetch all books
  const loadBooks = async () => {
    try {
      const res = await axios.get('/api/books');
      setBooks(res.data);
      console.log("Books loaded:", res.data);
    } catch (err) {
      console.error("Error loading books:", err);
      alert("Failed to load books.");
    }
  };

  // ✅ Delete a book by ISBN
  const deleteBook = async (isbn) => {
    if (!window.confirm(`Are you sure you want to delete book with ISBN ${isbn}?`)) return;
    try {
      await axios.delete(`/api/books/${isbn}`);
      alert("Book deleted successfully.");
      loadBooks(); // reload after delete
    } catch (err) {
      console.error("Error deleting book:", err);
      alert("Failed to delete book.");
    }
  };

  // ✅ Load books on mount
  useEffect(() => {
    loadBooks();
  }, []);

  return (
    <div style={{ textAlign: 'center' }}>
      <h2>Book List</h2>
      <button onClick={() => navigate('/add')}>Add Book</button>
      <br /><br />
      {books.length === 0 ? (
        <p>No books found.</p>
      ) : (
        <ul style={{ listStyle: 'none', padding: 0 }}>
          {books.map(book => (
            <li key={book.isbn} style={{ margin: '10px 0', borderBottom: '1px solid #ccc', paddingBottom: '10px' }}>
              <b>{book.title}</b> by {book.author} ({book.publicationYear})<br />
              <span>ISBN: {book.isbn}</span><br /><br />
              <button onClick={() => navigate(`/edit/${book.isbn}`)}>Edit</button>
              <button onClick={() => deleteBook(book.isbn)} style={{ marginLeft: '10px' }}>Delete</button>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}

export default BookList;
