import React, { useEffect, useState } from 'react';
import axios from '../services/api';
import { useNavigate, useParams } from 'react-router-dom';

function EditBook() {
  const [book, setBook] = useState({ isbn: '', title: '', author: '', publicationYear: '' });
  const { isbn } = useParams();
  const navigate = useNavigate();

  useEffect(() => {
    axios.get(`/api/books/${isbn}`).then(res => setBook(res.data));
  }, [isbn]);

  const handleUpdate = async () => {
    await axios.put(`/api/books/${isbn}`, book);
    navigate('/books');
  };

  return (
    <div style={{ textAlign: 'center' }}>
      <h2>Edit Book</h2>
      <input value={book.isbn} disabled />
      <br /><br />
      <input value={book.title} onChange={e => setBook({ ...book, title: e.target.value })} />
      <br /><br />
      <input value={book.author} onChange={e => setBook({ ...book, author: e.target.value })} />
      <br /><br />
      <input type="number" value={book.publicationYear} onChange={e => setBook({ ...book, publicationYear: e.target.value })} />
      <br /><br />
      <button onClick={handleUpdate}>Update</button>
    </div>
  );
}

export default EditBook;
